import * as types from "../../actionTypes";
import TeachersApi from "../../../api/TeachersApi";

let actionsFunction = {

  //CRUD METHODS

  // Create teachers
  createTeachers: function(teachers) {
    return function(dispatch) {
      return TeachersApi
        .createTeachers(teachers)
        .then(teachers => {
          dispatch(actionsFunction.createTeachersSuccess(teachers));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createTeachersSuccess: function(teachers) {
    return { type: types.CREATE_TEACHERS_SUCCESS, payload: teachers };
  },


  // Delete teachers
  deleteTeachers: function(id) {
    return function(dispatch) {
      return TeachersApi
        .deleteTeachers(id)
        .then(teachers => {
          dispatch(actionsFunction.deleteTeachersSuccess(teachers));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteTeachersSuccess: function(teachers) {
    return { type: types.DELETE_TEACHERS_SUCCESS, payload: teachers };
  },


  // Find by _classes
  findBy_classes: function(key) {
    return function(dispatch) {
      return TeachersApi
        .findBy_classes(key)
        .then(item => {
          dispatch(actionsFunction.findBy_classesSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findBy_classesSuccess: function(item) {
    return { type: types.FINDBY_CLASSES_TEACHERS_SUCCESS, payload: item };
  },


  // Find by _clubs
  findBy_clubs: function(key) {
    return function(dispatch) {
      return TeachersApi
        .findBy_clubs(key)
        .then(item => {
          dispatch(actionsFunction.findBy_clubsSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findBy_clubsSuccess: function(item) {
    return { type: types.FINDBY_CLUBS_TEACHERS_SUCCESS, payload: item };
  },


  // Get teachers
  loadTeachers: function(id) {
    return function(dispatch) {
      return TeachersApi
        .getOneTeachers(id)
        .then(teachers => {
          dispatch(actionsFunction.loadTeachersSuccess(teachers));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadTeachersSuccess: function(teachers) {
    return { type: types.GET_TEACHERS_SUCCESS, payload: teachers };
  },

  // Load  list
  loadTeachersList: function() {
    return function(dispatch) {
      return TeachersApi
        .getTeachersList()
        .then(list => {
          dispatch(actionsFunction.loadTeachersListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadTeachersListSuccess: function(list) {
    return { type: types.LIST_TEACHERS_SUCCESS, payload: list };
  },

	
  // Save teachers
  saveTeachers: function(teachers) {
    return function(dispatch) {
      return TeachersApi
        .saveTeachers(teachers)
        .then(teachers => {
          dispatch(actionsFunction.saveTeachersSuccess(teachers));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveTeachersSuccess: function(teachers) {
    return { type: types.UPDATE_TEACHERS_SUCCESS, payload: teachers };
  },


};

export default actionsFunction;
